import React, { useState, useEffect } from "react";

import { Container, TaskItem } from "./styles";
import Button from "../../styles/components/Button";
import api from "../../services/api";

export default function Task({ history }) {
  const [task, setTask] = useState([]);
  useEffect(() => {
    const getTask = async () => {
      const response = await api.get("/tasks");
      setTask(response.data.docs);
    };
    getTask();
  }, []);

  async function handleSubmitTask() {
    history.push("/ads");
  }

  async function handleSubmitUser() {
    history.push("/signup");
  }

  return (
    <div>
      <Container>
        <header>
          <h1>Tarefas:</h1>
          <div>
            <Button onClick={handleSubmitUser}>+ Usuário</Button>
            <Button onClick={handleSubmitTask}>+ Tarefa</Button>
          </div>
        </header>
        <div>
          {task.map(function(object) {
            return (
              <TaskItem key={object._id}>
                <p>Nome: {object.name}</p>
                <p>Descrição: {object.description}</p>
                <p>Estimativa de horas: {object.estimate}</p>
                <p>Autor: {object.author.name}</p>
              </TaskItem>
            );
          })}
        </div>
      </Container>
    </div>
  );
}
