import React, { useState, useEffect } from "react";
import { toast } from "react-toastify";
import api from "../../services/api";
import { login } from "../../services/auth";

import Button from "../../styles/components/Button";
import { Container, SignForm } from "./styles";

export default function SignUp({ history }) {
  const [loading, setLoading] = useState("disabled");
  const [ads, setAds] = useState({ name: "", email: "", password: "" });

  useEffect(() => {
    const isEnabled = () => {
      return user.email.length > 5 && user.password.length >= 6
        ? setLoading("")
        : setLoading("disabled");
    };
    isEnabled();
  }, [user]);

  async function handleSubmit(e) {
    e.preventDefault();
    try {
      await api.post(`/users`, user);

      const userCreate = { email: user.email, password: user.password };

      const response = await api.post(`/sessions`, userCreate);

      const { token, user: userData } = response.data;

      if (token) {
        login(token, userData);
      }

      history.push("/home");
    } catch (err) {
      toast.error("Email ou senha inválido!");
    }
  }

  function handleInputChange(e) {
    const { id, value } = e.target;
    setAds({
      ...ads,
      [id]: value
    });
  }

  return (
    <Container>
      <SignForm onSubmit={handleSubmit}>
        <h1>Cadastro de anúncio</h1>

        <span>TÍTULO</span>
        <input
          id="title"
          value={ads.title}
          required
          onChange={handleInputChange}
        />

        <span>DESCRIÇÃO</span>
        <input
          id="description"
          value={ads.description}
          required
          onChange={handleInputChange}
        />

        <span>PREÇO</span>
        <input
          id="price"
          type="number"
          value={ads.price}
          required
          onChange={handleInputChange}
        />

        <Button size="big" onClick={handleSubmit} type="submit">
          Cadastrar
        </Button>
      </SignForm>
    </Container>
  );
}
